import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable } from "rxjs";
import { ResponsePhoto } from "../interface/reponsePhoto.interface";
import { Produit } from "../models/produit.model";
import { produitsService } from "./produits.service";

@Injectable({
    providedIn: 'root'
})
export class uploadService implements ResponsePhoto{

    constructor(private httpClient: HttpClient,
                private produitService: produitsService,
                ){}

    nom!: string;

    public uploadFile(file: File): Observable<ResponsePhoto> {
        let formParams = new FormData();
        formParams.append('file', file);
        console.log(formParams);
        return this.httpClient.post<any>(`http://127.0.0.1:8000/api/post_image`, formParams);
    }

    public uploadProduit(formValue: {refProduit: string, shortLibel: string, longLabel: string, idCategorie: number, photo: string[], prixHt: number}, images: string[]): Observable<Produit>{
        return this.produitService.getAllProduit().pipe

    }

}
