div.container-fluid.col.col-9.mx-auto
<div class="card" style="width: 18rem">
  <img class="card-img-top" [src]="image" [alt]="nomination" />
  <div class="card-body">
    <h5 class="card-title">{{ nomination }}</h5>
    <div class="list-group">
      <a
        href="#"
        class="list-group-item list-group-item-action"
        *ngFor="let oneSousCat of sous_cat"
        >{{ oneSousCat }}</a
      >
    </div>
  </div>
</div>
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-categorie',
  templateUrl: './categorie.component.html',
  styleUrls: ['./categorie.component.scss']
})
export class CategorieComponent implements OnInit {
  id!: number;
  sous_cat!: string[];
  nomination!: string;
  image!: string;
  slug!: string;

  ngOnInit(){
    this.id=1;
    this.sous_cat=['jeux', 'nouveauté'];
    this.nomination='Playstation 5';
    this.image='https://images.pexels.com/photos/15686450/pexels-photo-15686450.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1';
    this.slug ='playstation-5'
  }
}
