import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { delay, Observable } from 'rxjs';
import { ResponsePhoto } from '../interface/reponsePhoto.interface';
import { Categorie } from '../models/categorie.model';
import { Photo } from '../models/photo.model';
import { Produit } from '../models/produit.model';
import { CategoriesService } from '../services/categories.service';
import { produitsService } from '../services/produits.service';
import { uploadService } from '../services/upload.servive';

@Component({
  selector: 'app-produit',
  templateUrl: './produit.component.html',
  styleUrls: ['./produit.component.scss']
})
export class ProduitComponent implements OnInit {
  @Input() produit!: Produit;
  @Input() photo!: Photo;


  categorie!: Categorie;
  produits!: Produit;
  produit$!: Observable<Produit>;
  majorPhoto$!: Observable<Photo[]>;
  otherPhotos$!: Observable<Photo[]>;


    constructor(private categoriesService: CategoriesService,
                private produitService: produitsService,
                private route: ActivatedRoute,
                private router: Router,
                private formbuilder: FormBuilder,
                private uploadService: uploadService,
                ){}

    ngOnInit(){
      const produitId = +this.route.snapshot.params['id'];
      this.produit$ = this.produitService.getOneProduit(produitId);
      this.otherPhotos$ = this.produitService.getOtherPictureOfOneProduit(produitId);
      this.majorPhoto$ = this.produitService.getMajorPictureOneProduit(produitId);

      this.photoPrincipalForm = this.formbuilder.group({
        photoprincipale: [null, Validators.required]
      })

      this.photoScondaireForm = this.formbuilder.group({
        photo: [null, Validators.required]
      })

      this.produitForm = this.formbuilder.group({
        refProduit: [null, Validators.required],
        shortLibel: [null, Validators.required],
        longLibel: [null, Validators.required],
        idCategorie: [null, Validators.required],
        prixHt: [null, Validators.required],
      })
    }

   }
<div *ngIf="produit$ | async as produit" class="container-fluid my-3 row">
  <div class="col-12 col-lg-6 mx-auto">
        <div class="d-flex flex-column">
            <div class="photo row">
                <h1>Photo produit</h1>
                <div class="photo-principale col-6 px-2 py-2" *ngFor="let photoPrincipale of majorPhoto$ | async">
                    <img src="http://127.0.0.1:8000/{{photoPrincipale.src}}" class="w-100" [alt]=photoPrincipale.refProduit>
                </div>
                <div class="photo-secondaire col-6 px-2 py-2" *ngFor="let otherPic of otherPhotos$ | async">
                    <img src="http://127.0.0.1:8000/{{otherPic.src}}" class="w-100" [alt]=otherPic.refProduit>
                </div>
            </div>
            <div class="details">
                <h1>Detail produit</h1>
                <ul class="list-group">
                    <li class="list-group-item">{{ produit.shortLibel }}</li>
                    <li class="list-group-item">{{ produit.refProduit }}</li>
                    <li class="list-group-item">{{ produit.longLibel }}</li>
                    <li class="list-group-item">{{ produit.prixHt | currency: 'EUR': true }}</li>
               </ul>
            </div>
        </div>
    </div>
    <div class="col-12 col-lg-6">
        <h1>Modifier le produit</h1>

</div>
