import { Injectable } from "@angular/core";
import { Observable } from "rxjs";
import { ReponseToken } from "../interface/reponseToken.interface";
import { HttpClient } from "@angular/common/http";

@Injectable({
    providedIn: "root"
})
export class AuthService{

    constructor(private httpClient: HttpClient,
                ) {}

    private Token!: string;
    private refresh_token!: string;

    authentification(formInfo: Object): Observable<ReponseToken> {
        return this.httpClient.post<any>('http://127.0.0.1:8000/auth', formInfo).subscribe((reponse: ReponseToken) => {
            this.Token = reponse.token,
            this.refresh_token = reponse.refresh_token
        });

    }

    getToken(){
        return this.Token
    }
}
