import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable } from "rxjs";
import { Categorie } from "../models/categorie.model";

@Injectable({
    providedIn: 'root'
})
export class CategoriesService {

    constructor (private http: HttpClient) {}



    categories: Categorie[] = [
        {
            id: 1,
            sous_cat: ['jeux', 'nouveauté'],
            nomination: 'Playstation 5',
            image: 'https://images.pexels.com/photos/15686450/pexels-photo-15686450.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
            slug: 'playstation-5'
        }, {
            id: 2,
            sous_cat: ['jeux', 'nouveauté'],
            nomination: 'X-box one',
            image: 'https://images.pexels.com/photos/139038/pexels-photo-139038.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
            slug: 'x-box-one'
        }, {
            id: 3,
            sous_cat: ['jeux', 'nouveauté'],
            nomination: 'Switch',
            image: 'https://images.pexels.com/photos/371924/pexels-photo-371924.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
            slug: 'switch'
        }
    ]

    getAllCategories(): Observable<Categorie[]> {
        return this.http.get<Categorie[]>('http://127.0.0.1:8000/api/categories?page=1&exists%5Bsous_cat%5D=false');
    }

    getCategorieById(categorieId: number): Categorie {
        const categorie = this.categories.find(categorie => categorie.id === categorieId);
        if (!categorie){
            throw new Error('Categorie not found');
        } else {
            return categorie;
        }
    }
}
