        photo: 'https://image.api.playstation.com/vulcan/ap/rnd/202302/2409/6fb9611d5b5aaefcfcef5bd13e0a4a4cbeb9b6d6cd2ad765.jpg',
        prixHt: 69.00,
    },{
        id: 5,
        refProduit: '3307216258384', 
        shortLibel: 'Assassin\'s Creed Mirage Edition Deluxe PS5 ', 
        longLibel: 'Découvrez l\'histoire de Basim, voleur à la tire habile cherchant des réponses et la justice dans les rues animées du Bagdad du IXe siècle. Par l\'intermédiaire d\'une société aussi ancienne que mystérieuse appelée Ceux qu\'on ne voit pas, il va devenir un tueur redoutable et son sort va prendre une tournure qu\'il n\'aurait jamais imaginée.', 
        idCategorie: 3,
        photo: 'https://generacionxbox.com/wp-content/uploads/2022/09/assasins-creed-mirage.jpg',
        prixHt: 59.00,
    }
    ]

    getAllProduits():Produit[] {
        return this.produits
    }

    getAllProduitsByCategorie(categorieId: number): Produit[] {
        return this.produits.filter((produit) => produit.idCategorie === categorieId);
        return this.http.get<Produit[]>(`http://127.0.0.1:8000/api/produits?page=1&idCategorie=${ca}`)
    }

    getOneProduit(produitId: number): Produit {
        const produit = this.produits.find(produit => produit.id === produitId);
        if(!produit){
            throw new Error ('Produit not found!');
        } else {
            return produit;
        }
    }

}
