import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { map, Observable } from 'rxjs';
import { Categorie } from '../models/categorie.model';
import { Produit } from '../models/produit.model';
import { CategoriesService } from '../services/categories.service';

@Component({
  selector: 'app-new-produit',
  templateUrl: './new-produit.component.html',
  styleUrls: ['./new-produit.component.scss']
})
export class NewProduitComponent implements OnInit {

  produitForm!: FormGroup;
  photoForm!: FormGroup;
  // photoTwoForm!: FormGroup;
  // photoThreeForm!: FormGroup;
  // photoFourForm!: FormGroup;
  // photoFiveForm!: FormGroup;
  produitpreview$!: Observable<Produit>;
  categories$!: Observable<Categorie[]>;

  constructor(private formbuilder: FormBuilder,
              private categoriesService: CategoriesService,
              private http: HttpClient,) { }

  ngOnInit(): void {
    this.categories$ = this.categoriesService.getAllSousCategories();

    this.produitForm = this.formbuilder.group({
      refProduit: [null, Validators.required],
      shortLibel: [null, Validators.required],
      longLibel: [null, Validators.required],
      idCategorie: [null, Validators.required],
      prixHt: [null, Validators.required],
    });
    this.produitpreview$ = this.produitForm.valueChanges.pipe(
      map(formValue => ({
        ...formValue,
        id: 0,
      }))
    );
    this.photoForm = this.formbuilder.group({
      photo: [null, Validators.required]
    });
    // this.photoTwoForm = this.formbuilder.group({
    //   photo2: [null, Validators.required]
    // });
    // this.photoThreeForm = this.formbuilder.group({
    //   photo3: [null, Validators.required]
    // });
    // this.photoFourForm = this.formbuilder.group({
    //   photo4: [null, Validators.required]
    // });
    // this.photoFiveForm = this.formbuilder.group({
    //   photo5: [null, Validators.required]
    // });
  }

  onSubmitForm(): void {
    console.log(this.produitForm.value);
  }

  onSubmitPhoto() {
    // const file: File = event.target.files[0];
    // if(file){
    //   this.fileName = file.name;
    //   const formData = new FormData();
    //   formData.append("thumbnail", file);
    //   const upload$ = this.http.post('http://127.0.0.1:8000/api/post_image', formData);
    //   upload$.subscribe();
    // }
  }

}
