import { Component, OnInit } from '@angular/core';
import { Categorie } from './models/categorie.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  oneCat!: Categorie;
  twoCat!: Categorie;
  threeCat!: Categorie;

  ngOnInit() {
    this.oneCat = new Categorie(
      1,
      ['jeux', 'nouveauté'],
      'Playstation 5',
      'https://images.pexels.com/photos/15686450/pexels-photo-15686450.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
      'playstation-5'
    );
    this.twoCat = new Categorie(
      1,
      ['jeux', 'nouveauté'],
      'X-box one',
      'https://images.pexels.com/photos/139038/pexels-photo-139038.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
      'x-box-one'
    );
    this.threeCat = new Categorie(
      1,
      ['jeux', 'nouveauté'],
      'Switch',
      'https://images.pexels.com/photos/371924/pexels-photo-371924.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
      'switch'
    )
  }
}
