import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Categorie } from '../models/categorie.model';
import { CategoriesService } from '../services/categories.service';

@Component({
  selector: 'app-categorie',
  templateUrl: './categorie.component.html',
  styleUrls: ['./categorie.component.scss']
})
export class CategorieComponent implements OnInit {
  @Input() categorie!: Categorie;

  constructor(private categoriesService: CategoriesService,
              private router: Router){}

  ngOnInit(){

  }

  onViewProduits(){
    this.router.navigateByUrl(`categories/${this.categorie.id}`)
  }
}
<div class="card mx-2 my-2 h-100" style="width: 18rem">
  <img class="card-img-top" [src]="categorie.image" [alt]="categorie.nomination"/>
  <div class="card-body">
    <h5 class="card-title">{{ categorie.nomination | uppercase }}</h5>
    <div class="list-group">
      <a href="#" class="list-group-item list-group-item-action " aria-current="true">The current link item</a>
      <a href="#" class="list-group-item list-group-item-action">A second link item</a>
      <a href="#" class="list-group-item list-group-item-action">A third link item</a>
      <a href="#" class="list-group-item list-group-item-action">A fourth link item</a>
      <a href="#" class="list-group-item list-group-item-action disabled" tabindex="-1" aria-disabled="true">A disabled link item</a>
    </div>
   <button (click)="onViewProduits()" class="btn btn-outline-success">Voir les produits</button>
  </div>
</div>
