import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-new-produit',
  templateUrl: './new-produit.component.html',
  styleUrls: ['./new-produit.component.scss']
})
export class NewProduitComponent implements OnInit{

  produitForm!: FormGroup;
  produit

  constructor(private formbuilder: FormBuilder){}

  ngOnInit(): void {
    this.produitForm = this.formbuilder.group({
       ref_produit: [null],
       short_libel: [null],
       long_libel: [null],
       prix_ht: [null],
       slug: [null],
    })
  }

  onSubmitForm():void {
    console.log(this.produitForm.value)
  }

}
<div class="container-fluid col col-5 mx-auto text-center">
    <h2 class="text-uppercase text-white">Rajouter un nouveau produit</h2>
    <form [formGroup]="produitForm">
        <div class="mb-3">
            <label for="ref_produit" class="form-label text-white">Référence du produit</label>
            <input type="text" class="form-control" id="ref_produit" formControlName="ref_produit">
          </div>
          <div class="mb-3">
            <label for="short_libel" class="form-label text-white">Titre</label>
            <input type="text" class="form-control" id="short_libel" formControlName="short_libel">
          </div>
          <div class="mb-3">
            <label for="long_libel" class="form-label text-white">Description</label>
            <textarea class="form-control" id="long_libel" formControlName="long_libel"></textarea>
          </div>
          <div class="mb-3">
            <label for="prix_ht" class="form-label text-white">Prix HT</label>
            <input type="text" class="form-control" id="prix_ht" formControlName="prix_ht">
          </div>
          <div class="mb-3">
            <label for="slug" class="form-label text-white">Slug</label>
            <input type="text" class="form-control" id="slug" formControlName="slug">
          </div>

          <button (click)="onSubmitForm()" type="submit" class="btn btn-primary">Rajouter</button>
    </form>
</div>
