import { Component, OnInit } from '@angular/core';
import { Categorie } from '../models/categorie.model';
import { CategoriesService } from '../services/categories.service';

@Component({
  selector: 'app-categorie-list',
  templateUrl: './categorie-list.component.html',
  styleUrls: ['./categorie-list.component.scss']
})
export class CategorieListComponent implements OnInit {

  categories!: Categorie[];

  constructor(private categorieService: CategoriesService){}

  ngOnInit() {
    this.categories = [
      {
        id: 1,
      sous_cat: ['jeux', 'nouveauté'],
      nomination: 'Playstation 5',
      image: 'https://images.pexels.com/photos/15686450/pexels-photo-15686450.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
      slug: 'playstation-5'
      },{
      id: 2,
      sous_cat: ['jeux', 'nouveauté'],
      nomination: 'X-box one',
      image: 'https://images.pexels.com/photos/139038/pexels-photo-139038.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
      slug: 'x-box-one'
      },{
       id: 3,
      sous_cat: ['jeux', 'nouveauté'],
      nomination: 'Switch',
      image: 'https://images.pexels.com/photos/371924/pexels-photo-371924.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1',
      slug: 'switch'
      }
    ]

  }
}
